(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");console.log(e);const s=setInterval((()=>{(()=>{let m=(()=>{let t=(new Date(e).getTime()-(new Date).getTime())/1e3;return Math.floor(t/60/60/24),{timeRemaining:t,hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();m.timeRemaining>0&&(m.hours<=9?t.textContent="0"+m.hours:t.textContent=m.hours,m.minutes<=9?n.textContent="0"+m.minutes:n.textContent=m.minutes,m.seconds<=9?o.textContent="0"+m.seconds:o.textContent=m.seconds,m.timeRemaining<=0&&clearInterval(s))})()}),1e3);console.log(setInterval)})("31 june 2022")})();